const fs = require('fs');
const config = require('./data.js')


fs.appendFile('index.js',config.fileConfig,'utf8',(err)=>{
  if(err) throw err;
  console.log('data was appended');
});


fs.appendFile('db.js',config.dbConfig,'utf8',(err)=>{
  if(err) throw err;
  console.log('data was appended');
});

(async function(){
  await fs.mkdir('./models',((err)=>{
    if(err)throw err;
    console.log('dir created')
  }));
  fs.appendFile('./models/model.js',config.model,'utf8',(err)=>{
    if(err) throw err;
    console.log('data was appended');
  });
})();
